import React from 'react';
import { Box, Button, Paper, Typography, Chip, Stack, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TablePagination, Dialog, DialogTitle, DialogContent, DialogActions, TextField, FormControlLabel, Checkbox, useTheme, Select, MenuItem, FormControl, InputLabel } from '@mui/material';

export default function CadastroUsuarios() {
  const theme = useTheme();
  const [rows, setRows] = React.useState([]);
  const [selectedUser, setSelectedUser] = React.useState(null);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [total, setTotal] = React.useState(0);
  const [openModal, setOpenModal] = React.useState(false);
  const [openEditModal, setOpenEditModal] = React.useState(false);
  const [editingUser, setEditingUser] = React.useState(null);
  const [form, setForm] = React.useState({ 
    username: '', 
    password: '', 
    nome: '', 
    email: '', 
    nivel_acesso: 'visualizacao',
    ativo: true,
    grupo_id: null 
  });
  const [editForm, setEditForm] = React.useState({ 
    username: '', 
    password: '', 
    nome: '', 
    email: '', 
    nivel_acesso: 'visualizacao',
    ativo: true,
    grupo_id: null 
  });
  const [grupos, setGrupos] = React.useState([]);
  const [saving, setSaving] = React.useState(false);
  const API = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  const loadPersisted = async () => {
    try {
      const [usuariosResp, gruposResp] = await Promise.all([
        fetch(`${API}/usuarios/`),
        fetch(`${API}/grupos/`)
      ]);
      
      if (usuariosResp.ok) {
        const data = await usuariosResp.json();
        if (data) {
          setRows((data || []).map((r, i) => ({ id: r.id ?? i + 1, ...r })));
          setTotal((data || []).length);
        }
      }
      
      if (gruposResp.ok) {
        const gruposData = await gruposResp.json();
        setGrupos(gruposData || []);
      }
    } catch (e) { console.error(e); }
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setEditForm({
      username: user.username,
      password: '', // Deixar vazio para não alterar
      nome: user.nome || '',
      email: user.email || '',
      nivel_acesso: user.nivel_acesso,
      ativo: user.ativo,
      grupo_id: user.grupo_id || null
    });
    setOpenEditModal(true);
  };

  const handleUpdateUser = async () => {
    setSaving(true);
    try {
      const updateData = { ...editForm };
      if (!updateData.password) {
        // Se senha estiver vazia, não enviar para não alterar
        delete updateData.password;
      }
      
      const resp = await fetch(`${API}/usuarios/${editingUser.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData)
      });
      if (!resp.ok) throw new Error('Erro ao atualizar usuário');
      setOpenEditModal(false);
      setEditingUser(null);
      await loadPersisted();
    } catch (e) {
      alert('Erro ao atualizar usuário.');
    } finally {
      setSaving(false);
    }
  };

  const handleImportExcel = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: 'binary' });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const data = XLSX.utils.sheet_to_json(ws, { defval: '' });
      const keys = data.length ? Object.keys(data[0]) : [];
      const cols = keys.map((k) => ({ field: k, headerName: k, flex: 1, minWidth: 130, editable: true }));
      const rows = data.map((r, i) => ({ id: i + 1, ...r }));
      setExcelColumns(cols);
      setExcelRows(rows);
    };
    reader.readAsBinaryString(file);

    try {
      const fd = new FormData();
      fd.append('file', file);
      const resp = await fetch(`${API}/uploads/cadastro_usuarios`, { method: 'POST', body: fd });
      if (!resp.ok) {
        let detail = '';
        try { const j = await resp.json(); detail = j?.detail || JSON.stringify(j); } catch {}
        throw new Error(`Falha ao enviar arquivo (${resp.status})${detail ? `: ${detail}` : ''}`);
      }
      const saved = await resp.json();
      setCurrentUploadId(saved?.id || null);
      await loadUploads();
    } catch (err) {
      console.error(err);
      alert(`Não foi possível salvar o arquivo no servidor.\n${err?.message || ''}`);
    }
  };

  const loadUploads = async () => {
    try {
      const resp = await fetch(`${API}/uploads?entidade=cadastro_usuarios`);
      const data = await resp.json();
      setUploads(data);
    } catch (e) { console.error(e); }
  };

  const loadHistory = async () => {
    try {
      const resp = await fetch(`${API}/tabelas/cadastro_usuarios/historico?limit=10`);
      if (!resp.ok) return;
      const data = await resp.json();
      setHistory(data || []);
    } catch (e) { console.error(e); }
  };

  React.useEffect(() => { loadPersisted(); }, []);

  const handleSelectVersion = async (e) => {
    const id = e.target.value;
    setSelectedVersion(id);
    if (!id) return;
    try {
      const resp = await fetch(`${API}/tabelas/cadastro_usuarios/${id}`);
      if (!resp.ok) return;
      const data = await resp.json();
      const rows = (data?.dados || []).map((r, i) => ({ id: r.id ?? i + 1, ...r }));
      const keys = rows.length ? Object.keys(rows[0]).filter(k => k !== 'id') : [];
      const cols = keys.map((k) => ({ field: k, headerName: k, flex: 1, minWidth: 130, editable: true }));
      setExcelColumns(cols);
      setExcelRows(rows);
    } catch (e) { console.error(e); }
  };



  const handleProcessRowUpdate = (newRow) => {
    setExcelRows((prev) => prev.map((r) => (r.id === newRow.id ? newRow : r)));
    return newRow;
  };

  // Cabeçalho e ações
  return (
    <Box sx={{ backgroundColor: theme.palette.background.default, minHeight: '100vh', p: { xs: 1, md: 4 } }}>
      <Box sx={{ maxWidth: 1200, mx: 'auto', mt: 3 }}>
        <Typography variant="h4" sx={{ fontWeight: 700, mb: 1 }}>Usuários</Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Typography variant="subtitle1" sx={{ mr: 2 }}>Total de resultados:</Typography>
          <Chip label={total} color="primary" sx={{ fontWeight: 700, fontSize: 16 }} />
          <Box sx={{ flex: 1 }} />
          <Button variant="contained" color="success" sx={{ mr: 2 }}>Filtrar</Button>
          <Button variant="contained" color="primary" onClick={() => setOpenModal(true)}>Criar novo usuário</Button>
      {/* Modal de cadastro */}
      <Dialog open={openModal} onClose={() => setOpenModal(false)}>
        <DialogTitle>Novo Usuário</DialogTitle>
        <DialogContent sx={{ minWidth: 340 }}>
          <TextField
            label="Usuário"
            fullWidth
            margin="normal"
            value={form.username}
            onChange={e => setForm(f => ({ ...f, username: e.target.value }))}
          />
          <TextField
            label="Nome Completo"
            fullWidth
            margin="normal"
            value={form.nome}
            onChange={e => setForm(f => ({ ...f, nome: e.target.value }))}
          />
          <TextField
            label="E-mail"
            type="email"
            fullWidth
            margin="normal"
            value={form.email}
            onChange={e => setForm(f => ({ ...f, email: e.target.value }))}
          />
          <TextField
            label="Senha"
            type="password"
            fullWidth
            margin="normal"
            value={form.password}
            onChange={e => setForm(f => ({ ...f, password: e.target.value }))}
          />
          <FormControl fullWidth margin="normal">
            <InputLabel>Nível de Acesso</InputLabel>
            <Select
              value={form.nivel_acesso}
              label="Nível de Acesso"
              onChange={e => setForm(f => ({ ...f, nivel_acesso: e.target.value }))}
            >
              <MenuItem value="admin">Admin (Acesso Total)</MenuItem>
              <MenuItem value="willians">Willians (Acesso Total)</MenuItem>
              <MenuItem value="manutencao">Manutenção (Apenas Testes)</MenuItem>
              <MenuItem value="visualizacao">Visualização</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth margin="normal">
            <InputLabel>Grupo de Usuários</InputLabel>
            <Select
              value={form.grupo_id || ''}
              label="Grupo de Usuários"
              onChange={e => setForm(f => ({ ...f, grupo_id: e.target.value || null }))}
            >
              <MenuItem value="">Sem grupo</MenuItem>
              {grupos.map(grupo => (
                <MenuItem key={grupo.id} value={grupo.id}>
                  {grupo.nome}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <FormControlLabel
            control={<Checkbox checked={form.ativo} onChange={e => setForm(f => ({ ...f, ativo: e.target.checked }))} />}
            label="Usuário Ativo"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenModal(false)} disabled={saving}>Cancelar</Button>
          <Button variant="contained" onClick={async () => {
            setSaving(true);
            try {
              const resp = await fetch(`${API}/usuarios/`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(form)
              });
              if (!resp.ok) throw new Error('Erro ao criar usuário');
              setOpenModal(false);
              setForm({ 
                username: '', 
                password: '', 
                nome: '', 
                email: '', 
                nivel_acesso: 'visualizacao',
                ativo: true,
                grupo_id: null 
              });
              await loadPersisted();
            } catch (e) {
              alert('Erro ao criar usuário.');
            } finally {
              setSaving(false);
            }
          }} disabled={saving || !form.username || !form.password || !form.nome}>Salvar</Button>
        </DialogActions>
      </Dialog>
        </Box>
        {/* Chips de filtro exemplo */}
        <Stack direction="row" spacing={1} sx={{ mb: 2 }}>
          <Chip label="E-mail" onDelete={() => {}} />
          <Chip label="Tipo de conta" onDelete={() => {}} />
        </Stack>
        <TableContainer component={Paper} sx={{ borderRadius: 2, boxShadow: 0 }}>
          <Table>
            <TableHead>
              <TableRow sx={{ backgroundColor: '#f4f6f8' }}>
                <TableCell sx={{ fontWeight: 700 }}>Nome</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>E-mail</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Nível de Acesso</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Status</TableCell>
                <TableCell sx={{ fontWeight: 700 }}>Ações</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row, idx) => (
                <TableRow key={row.id || idx} hover sx={{ borderBottom: '1px solid #e0e0e0' }}>
                  <TableCell>{row.nome || row.username || row.Nome || row.name || ''}</TableCell>
                  <TableCell>{row.email || row['E-mail'] || row['e-mail'] || ''}</TableCell>
                  <TableCell>
                    <Chip 
                      label={
                        row.nivel_acesso === 'admin' ? 'Admin' :
                        row.nivel_acesso === 'willians' ? 'Willians' :
                        row.nivel_acesso === 'manutencao' ? 'Manutenção' :
                        'Visualização'
                      }
                      color={
                        row.nivel_acesso === 'admin' || row.nivel_acesso === 'willians' ? 'error' :
                        row.nivel_acesso === 'manutencao' ? 'warning' :
                        'default'
                      }
                      size="small"
                    />
                  </TableCell>
                  <TableCell>
                    <Chip 
                      label={row.ativo ? 'Ativo' : 'Inativo'}
                      color={row.ativo ? 'success' : 'default'}
                      size="small"
                    />
                  </TableCell>
                  <TableCell>
                    <Button size="small" variant="text" onClick={() => setSelectedUser(row)} sx={{ mr: 1 }}>
                      Ver detalhes
                    </Button>
                    <Button size="small" variant="outlined" onClick={() => handleEditUser(row)}>
                      Editar
                    </Button>
                  </TableCell>
      {/* Modal de detalhes do usuário */}
      <Dialog open={!!selectedUser} onClose={() => setSelectedUser(null)}>
        <DialogTitle>Detalhes do Usuário</DialogTitle>
        <DialogContent sx={{ minWidth: 340 }}>
          {selectedUser && (
            <Box>
              <Typography><b>Usuário:</b> {selectedUser.username}</Typography>
              <Typography><b>Nome:</b> {selectedUser.nome || '-'}</Typography>
              <Typography><b>E-mail:</b> {selectedUser.email || '-'}</Typography>
              <Typography><b>ID:</b> {selectedUser.id}</Typography>
              <Typography><b>Nível de Acesso:</b> {
                selectedUser.nivel_acesso === 'admin' ? 'Admin (Acesso Total)' :
                selectedUser.nivel_acesso === 'willians' ? 'Willians (Acesso Total)' :
                selectedUser.nivel_acesso === 'manutencao' ? 'Manutenção (Apenas Testes)' :
                'Visualização'
              }</Typography>
              <Typography><b>Status:</b> {selectedUser.ativo ? 'Ativo' : 'Inativo'}</Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedUser(null)}>Fechar</Button>
        </DialogActions>
      </Dialog>

      {/* Modal de edição de usuário */}
      <Dialog open={openEditModal} onClose={() => setOpenEditModal(false)}>
        <DialogTitle>Editar Usuário</DialogTitle>
        <DialogContent sx={{ minWidth: 340 }}>
          <TextField
            label="Usuário"
            fullWidth
            margin="normal"
            value={editForm.username}
            onChange={e => setEditForm(f => ({ ...f, username: e.target.value }))}
          />
          <TextField
            label="Nome Completo"
            fullWidth
            margin="normal"
            value={editForm.nome}
            onChange={e => setEditForm(f => ({ ...f, nome: e.target.value }))}
          />
          <TextField
            label="E-mail"
            type="email"
            fullWidth
            margin="normal"
            value={editForm.email}
            onChange={e => setEditForm(f => ({ ...f, email: e.target.value }))}
          />
          <TextField
            label="Nova Senha (deixe em branco para não alterar)"
            type="password"
            fullWidth
            margin="normal"
            value={editForm.password}
            onChange={e => setEditForm(f => ({ ...f, password: e.target.value }))}
          />
          <FormControl fullWidth margin="normal">
            <InputLabel>Nível de Acesso</InputLabel>
            <Select
              value={editForm.nivel_acesso}
              label="Nível de Acesso"
              onChange={e => setEditForm(f => ({ ...f, nivel_acesso: e.target.value }))}
            >
              <MenuItem value="admin">Admin (Acesso Total)</MenuItem>
              <MenuItem value="willians">Willians (Acesso Total)</MenuItem>
              <MenuItem value="manutencao">Manutenção (Apenas Testes)</MenuItem>
              <MenuItem value="visualizacao">Visualização</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth margin="normal">
            <InputLabel>Grupo de Usuários</InputLabel>
            <Select
              value={editForm.grupo_id || ''}
              label="Grupo de Usuários"
              onChange={e => setEditForm(f => ({ ...f, grupo_id: e.target.value || null }))}
            >
              <MenuItem value="">Sem grupo</MenuItem>
              {grupos.map(grupo => (
                <MenuItem key={grupo.id} value={grupo.id}>
                  {grupo.nome}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <FormControlLabel
            control={<Checkbox checked={editForm.ativo} onChange={e => setEditForm(f => ({ ...f, ativo: e.target.checked }))} />}
            label="Usuário Ativo"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenEditModal(false)} disabled={saving}>Cancelar</Button>
          <Button 
            variant="contained" 
            onClick={handleUpdateUser} 
            disabled={saving || !editForm.username}
          >
            Salvar
          </Button>
        </DialogActions>
      </Dialog>
                </TableRow>
              ))}
            </TableBody>
          </Table>
          <TablePagination
            component="div"
            count={rows.length}
            page={page}
            onPageChange={(_, newPage) => setPage(newPage)}
            rowsPerPage={rowsPerPage}
            onRowsPerPageChange={e => { setRowsPerPage(parseInt(e.target.value, 10)); setPage(0); }}
            rowsPerPageOptions={[5, 10, 25]}
            labelRowsPerPage="Linhas por página:"
          />
        </TableContainer>
      </Box>
    </Box>
  );
}
